export default (state , action)=>{
    switch(action.type){
        case 'detele_Transaction':
            return{
                ...state,
                transactions:state.transactions.filter(e =>
                 e.id !==action.payload   
                )
            }
            case 'add_Transaction':
                return{
                    ...state,
                    transactions:[...state.transactions]   
                    
                }
        default : 
            return state
    }
}
